//msg.google_home_announcement = "Daddy just made fifty bucks!"
// return msg;

msg.google_home_announcement = "We have just won another " + msg.new_medal_type + " medal! " + msg.new_medal_ranking

return msg;

msg.topic = "diff"
msg.payload = msg.gold_medals - msg.current_gold;
return msg;

msg.topic = "diff"
msg.payload = msg.silver_medals - msg.current_silver;
return msg;

msg.topic = "diff"
msg.payload = msg.bronze_medals - msg.current_bronze;
return msg;

msg.topic = "diff"
msg.payload = msg.total_medals - msg.current_total;
return msg;

msg.topic = "diff"
msg.payload = msg.medal_ranking - msg.current_medal_ranking;
return msg;

// msg.medals = msg.medals.slice(1); // Remove the first match if global search was used
const matches = msg.medals.match(/(\d+) (\d+) (\d+) (\d+)/);
if (matches) {
    msg.gold_medals = parseInt(matches[1]);
    msg.silver_medals = parseInt(matches[2]);
    msg.bronze_medals = parseInt(matches[3]);
    msg.total_medals = parseInt(matches[4]);
} else {
    // Handle cases where the regex doesn't match (optional)
    node.error("Invalid medal string format");
}

return msg;
